import React, { useEffect, useState, useContext, useRef } from 'react';
import './MetaWidget.scss'
import Link from 'next/link'

const MetaWidget = props => {
    const [ state, setState ] = useState({});
    useEffect(() => {
        console.log(props.data )
    }, [props]);


    const renderMeta = props.data.map(meta=>{
        return (
            <div className='meta-widget-item'>
                <p>{meta.name}</p>
                <Link href={window.location.origin + '/' }><a>{meta.name}</a></Link>
            </div>
        )
    })
    return (
        <div className='meta-widget'>
            {renderMeta}
        </div>
    );
};
export default MetaWidget;
