import React, { useEffect, useState, useContext, useRef } from 'react';
import './AddWidgetWithPositionMenu.scss'

const AddWidgetWithPositionMenu = props => {
    const [ state, setState ] = useState({
        open:false
    });
    useEffect(() => {
    }, []);

    const onOpenHandler =()=>{
        state.open?setState({
            ...state,
            open: false
        }):setState({
            ...state,
            open: true
        })
    }

    if (state.open){
        return (
            <div className='AddWidgetWithPositionMenu'>
                <button onClick={()=>onOpenHandler()}>{props.type}</button>
                <div className="AddWidgetWithPositionMenuPositions">
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Home Page</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Home Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Post Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Posts Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Tags Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Categories Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Actors Page Sidebar</button>
                    <button className='AddWidgetWithPositionMenuPositionsBtn'>Footer</button>
                </div>
            </div>
        );
    }else {
        return (
            <div className='AddWidgetWithPositionMenu'>
                <button onClick={()=>onOpenHandler()}>{props.type}</button>
            </div>
        );
    }


};
export default AddWidgetWithPositionMenu;
