    //                 description : post.description.en,
    //                 disLikes : 0,
    //                 mainThumbnail:post.imageUrl,
    //                 videoTrailerUrl :post.imagePreviewUrl,
    //                 videoEmbedCode:post.iframe,
    //                 likes : 0,
    //                 duration:post.duration,
    //                 quality : post.quality,
    //                 status : 'published',
    //                 postType : "video",
    //                 sourceSite : "Xhamster",
    //                 views : 0,
    //                 lastModify:Date.now()
    //             };
