    //             //     categories:post.categories,
    //             //     actors:post.actors,
    //             //     tags:post.tags,
    //             //     mainThumbnail:post.imageUrl,
    //             //     status:post.status,
    //             //     type:post.type
    //             // };
    //             await contextData.functions.savePosts(data)
    //             // console.log(post. )
    //         })
    //
    //     })
    // };

    const renderItems = Object.keys(state).map(item=>{

        const onHoverHandler = state[item].subItems.map(subItem=>{
            if (hovered === item){
                return(
                    <Link href={state[item].pathURL+'/'+subItem}><a className='SideBarItem-SubItem'>{subItem}</a></Link>
                )
            }else return null

        })

        return (
            <div key={item} className='SideBarItemElement'>
                <div className='SideBarItemTitle'>
                    <Link href={state[item].pathURL}><a className='SideBarItem' onMouseOver={()=>setHovered(item)}>{item}</a></Link>
                    <button onClick={()=>hovered===item?setHovered(''):setHovered(item)}><img src={hovered===item?SortDownSvg:SortUpSvg} alt=""/></button>
                </div>

                <div className='SideBarItemElementSubItems'>
                    {onHoverHandler}
                </div>
            </div>
        )
    })



if (contextData.settings.adminPanelSideBar){
    return (
        <div className='SideBar'>
            {renderItems}
            {/*<button onClick={()=>generateFakeData()}>take fake data</button>*/}
        </div>
    );
}else return null
};
export default SideBar;
