import React,{useEffect,useState,useContext} from 'react';
import Switch from "react-switch";
import './IsInSlideShow.scss';
import { AppContext } from "../../../../../context/AppContext";


const IsInSlideShow = props => {
    const contextData = useContext(AppContext);
    const [state, setState] = useState({
    });
    useEffect(() => {
        console.log(props)
    }, []);
    return (
        <div className='IsInSlideShow VideoInformationSection'>
            <div className="title">
                <p>Slide Show</p>
            </div>
            <div className="editor ">
                <Switch onChange={e=>{
                    e.target.name = 'inSlideShow';
                    props.onChangeHandler(e)
                }} checked={contextData.editingPostData.inSlideShow} />
            </div>
        </div>
    );
};
export default IsInSlideShow;
