design.navigationStyle:
background: linear-gradient(to bottom,#1e1e1e 0,#000 100%);
height:60px;
.navigation-content{ 
    width: 100%;
    height: 60px;
    display: grid !important;
    grid-auto-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
    grid-template-areas: 'navigationMenu navigationMenu logo logo search-bar auth';
}

                             
@media only screen and (min-width: 768px) {
    display: flex;
    min-height: 100px;
    background: var(--navigation-background-color,#000);
    align-items: center;
    
    .navigation-content{
         min-height: 100px;
        top:0;
        position: relative;
        margin: 0 auto;
        padding: 0;
        max-width: 1325px;
        grid-template-rows: 60px 40px;
        grid-auto-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
        grid-template-areas: 'logo search-bar search-bar search-bar auth auth'
                             'navigationMenu navigationMenu navigationMenu navigationMenu navigationMenu navigationMenu'
        ;
    }
}

